mapper.update:
UPDATE DOCUMENTS
SET STATUS = #{status}, CURRENT_STEP = #{currentStep},
MODIFIER_ID = #{modifierId}, MODIFIER_NAME = #{modifierName}, MODIFIER_POSITION = #{modifierPosition}, MODIFIER_DEPT = #{modifierDept}, MODIFIER_TIME = #{modifierTime}
WHERE DOCUMENT_ID = #{documentId}

UPDATE APPROVAL_LINES
SET SIGN_ID = #{signId}, STATUS = #{status}, HANDLING_TIME = #{handlingTime}
WHERE DOCUMENT_ID = #{documentId} AND USER_ID = #{userId}

UPDATE APPROVAL_LINES
SET REJECT_REASON = #{rejectReason}, STATUS = #{status}, HANDLING_TIME = #{handlingTime}
WHERE DOCUMENT_ID = #{documentId} AND USER_ID = #{userId}

mapper.select:
SELECT A.*

FROM APPROVAL_LINES A

WHERE DOCUMENT_ID = #{documentId}

SELECT R.*

FROM REFERENCE_LINES R

WHERE DOCUMENT_ID = #{documentId}

mapper.update:
UPDATE DOCUMENTS
SET TITLE = #{title}, CONTENT = #{content}, STATUS = #{status}, WRITER_NAME = #{writerName}, WRITER_POSITION = #{writerPosition}, WRITER_DEPT = #{writerDept}, WRITER_TIME = #{writerTime}
WHERE DOCUMENT_ID = #{documentId}

UPDATE DOCUMENTS

SET
STATUS = #{status}

WHERE DOCUMENT_ID = #{documentId}
