mapper.select:
SELECT A.*, U.NAME AS USER_NAME, -- 결재자 이름 U.POSITION AS USER_POSITION -- 결재자 직급 FROM APPROVAL_LINES A
JOIN USERS U ON A.USER_ID = U.USER_ID
WHERE DOCUMENT_ID = #{documentId}

SELECT R.*, U.NAME AS USER_NAME, -- 참조자 이름 U.POSITION AS USER_POSITION -- 참조자 직급 FROM REFERENCE_LINES R
JOIN USERS U ON R.USER_ID = U.USER_ID
WHERE DOCUMENT_ID = #{documentId}
